import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Container, Row, Col } from 'react-bootstrap';

function App() {
  const names = ['John', 'Jane', 'Bob', 'Alice', 'Charlie'];
  const [randomName, setRandomName] = useState(null);

  const generateRandomName = () => {
    const randomIndex = Math.floor(Math.random() * names.length);
    setRandomName(names[randomIndex]);
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h1 className="mb-4">Random Name Generator</h1>
          <Button variant="primary" onClick={generateRandomName}>
            Generate Random Name
          </Button>
          {randomName && <p className="mt-3">Random Name: {randomName}</p>}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
